(()=>{"use strict";const e=e=>{const t=document.querySelector("#table-body");t.innerHTML="",e.forEach((e=>{t.insertAdjacentHTML("beforeend",`\n            <tr>\n                <th scope="row">${e.id+1}</th>\n                <td>${e.name}</td>\n                <td>${e.email}</td>\n                <td>${e.children?"есть":"нет"}</td>\n                <td>\n                    <div class="form-check form-switch">\n                        <input class="form-check-input" type="checkbox" role="switch"\n                            id="form-children" ${e.permissions?"checked":""}>\n                    </div>\n                </td>\n                <td>\n                    <div class="btn-group btn-group-sm" role="group" aria-label="Basic example">\n                        <button type="button" class="btn btn-warning">\n                            <i class="bi-pencil-square"></i>\n                        </button>\n                        <button type="button" class="btn btn-danger">\n                            <i class="bi-person-x"></i>\n                        </button>\n                     </div>\n                </td>\n             </tr>\n        `)}))};window.userService=new class{getUsers(){return fetch("http://localhost:4545/users").then((e=>e.json()))}addUser(e){return fetch("http://localhost:4545/users",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(e)}).then((e=>{e.json()}))}},userService.getUsers().then((t=>{e(t)})),(()=>{const t=document.querySelector("form"),n=t.querySelector("#form-name"),r=t.querySelector("#form-email"),s=t.querySelector("#form-children");t.addEventListener("submit",(t=>{t.preventDefault();const o={name:n.value,email:r.value,children:s.checked,permissions:!1};userService.addUser(o).then((()=>{userService.getUsers().then((t=>{e(t)}))}))}))})()})();